import { useState, useEffect } from 'react';
import { createContext } from 'react';

export const CartContext = createContext()

const { Provider } = CartContext

const CustomPovider = ({ children }) => {
 
 const [carro, setCarro] = useState(["Vino Rutini Regalo Estuche Madera X3 Edición Limitada"])
  const [quantity, setQuantity] = useState(0)
  console.log(carro)
 const comprar = (item) =>{
  console.log(carro)
   setCarro([ ...carro,item])
   
}
 
useEffect(()=>{
   setQuantity(carro.length)
 
},[carro])



return (
  <Provider value={{comprar, carro, setCarro, setQuantity, quantity}}>

  {children}
 </Provider>

 )
}
export default CustomPovider
