import { CardGroup, Container, Card, Col, Row, Button} from 'react-bootstrap';
import { CartContext } from '../context/CartContext';
import { useContext, useEffect } from 'react';

function Carrito(props) {

 const { carrito, quantity } = useContext(CartContext)
 useEffect(() => {
  // Actualiza el title de la página en cada click!
  document.title = `Has hecho clic  veces`
},[quantity])

   
    return (
        <>
            <Container className="mb-3" align="center" >
            <Row  >
    <Col sm={6} >
            <CardGroup>
            
                                    
      <Card border="secondary">
      <Card.Img variant="top" style={{ width: '150px' }}  />
     <Card.Body>
        <Card.Title>{carrito}

       </Card.Title>
       <Card.Text>

         Some quick example text to build on the card title and make up the bulk of
         the card's content.
                   </Card.Text>

                 <Button  variant="warning">COMPRAR</Button>
                                
            <Card.Text>
           <br/>                         
       {quantity}
       </Card.Text>
       </Card.Body>
       </Card>
          
                    
            </CardGroup>
            </Col>
            </Row>
            </Container>
            
                   </>
    );

}  export default Carrito;
