 const comprar = (item) =>{
  console.log(carro)
 setCarro([ ...carro,item])
   
}
 
useEffect(()=>{
   setQuantity(carro.length)
 
},[carro])



return (
  <UnComponente.Provider value={{comprar, carro, setCarro, setQuantity, quantity}}>

